    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        String valorHora;
        Double totalHoras;

        String dia1=txt1.getText().toString();
        String dia2=txt2.getText().toString();
        String dia3=txt3.getText().toString();
        String dia4=txt4.getText().toString();
        String dia5=txt5.getText().toString();
        String dia6=txt6.getText().toString();
        String dia7=txt7.getText().toString();
        double d1=Double.parseDouble(dia1);
        double d2=Double.parseDouble(dia2);
        double d3=Double.parseDouble(dia3);
        double d4=Double.parseDouble(dia4);
        double d5=Double.parseDouble(dia5);
        double d6=Double.parseDouble(dia6);
        double d7=Double.parseDouble(dia7);

        
        //funcion total de horas sumando dia 1 -al 7
        double horas=totalHoras(d1, d2, d3, d4, d5, d6, d7);       
               
         //tomar el valor de costo por hora del form PanelCosto
        String i=PanelCosto.txtprecio.getText().toString();
        
        double p=Double.parseDouble(i);

        double respuesta=sueldo(horas,p);
        String tot=String.valueOf(respuesta);

        lblsueldo.setText(tot);
        String horaSemana=String.valueOf(horas);
        lblTotalhoras.setText(horaSemana);

    }//GEN-LAST:event_jButton1ActionPerformed

