    private static double sueldo(double valor, double horas) {
        uddi.NewWebService_Service service = new uddi.NewWebService_Service();
        uddi.NewWebService port = service.getNewWebServicePort();
        return port.sueldo(valor, horas);
    }

    private static double totalHoras(double dia1, double dia2, double dia3, double dia4, double dia5, double dia6, double dia7) {
        uddi.NewWebService_Service service = new uddi.NewWebService_Service();
        uddi.NewWebService port = service.getNewWebServicePort();
        return port.totalHoras(dia1, dia2, dia3, dia4, dia5, dia6, dia7);
    }
